mapper.delete:
  DELETE FROM sms_role WHERE id in(
  #{id}
)

mapper.select:
SELECT COUNT(*)
FROM sms_role
WHERE name = #{name}

select
  id
from sms_role WHERE name = #{name}

mapper.insert:
insert into sms_role ( name, description,
                       sort, gmt_create, gmt_modified
)
values ( #{name}, #{description},
         #{sort}, #{gmtCreate}, #{gmtModified}
       )
